class Ai1wm_Exclude_Directory_Filter extends RecursiveFilterIterator {

	protected $exclude = array();

	public function __construct( RecursiveIterator $recursiveIter, $exclude = array() ) {
		$this->exclude = $exclude;
		parent::__construct( $recursiveIter );
	}

	public function accept() {
		return ! in_array( $this->current()->getFilename(), $this->exclude );
	}

	public function getChildren() {
		return new self( $this->getInnerIterator()->getChildren(), $this->exclude );
	}
}
